<?phpfunction getmicrotime(){                                                        // Starten der Microzeit Berechnung    list($usec, $sec) = explode(" ",microtime());    return ((float)$usec + (float)$sec);}$time_start = getmicrotime();echo '<html>';echo '<link rel="shortcut icon" href="lib/favicon.ico" type="image/x-icon">';echo '<link rel="icon" href="lib/favicon.ico" type="image/x-icon">';echo '<meta name="author" content="Florian Asche">';echo '<title>Measurement and Management System - Smart Home</title>';//echo '<meta http-equiv="Content-Type" content="text/html; charset=UTF-8" />';echo '<meta http-equiv="content-type" content="text/html; charset=ISO-8859-1">';echo '<meta http-equiv="pragma" content="no-cache" />';?>